import React, { useState, useEffect } from 'react';
import './GradesPage.css';

function GradesPage({ grades, onBack }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [gradeFilter, setGradeFilter] = useState('');
  const [yearFilter, setYearFilter] = useState('');
  const [monthFilter, setMonthFilter] = useState('');
  const [sortBy, setSortBy] = useState('date-desc');

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  // ADD THIS: Safety check for grades prop
  if (!grades || grades.length === 0) {
    return (
      <div className="grades-page">
        <div className="grades-header">
          <button onClick={onBack} className="back-button">
            ← Back to Dashboard
          </button>
          <h1>All Grades</h1>
          <p className="subtitle">Complete academic transcript with filtering and sorting</p>
        </div>
        <div className="no-grades">
          <p>No grades available. Please check your student data.</p>
        </div>
      </div>
    );
  }

  // Extract unique values for filters
  const uniqueGrades = [...new Set(grades.map(g => g.grade))].sort();
  const uniqueYears = [...new Set(grades.map(g => g.monthYear.split('/')[1]))].sort().reverse();
  const uniqueMonths = [...new Set(grades.map(g => g.monthYear.split('/')[0]))].sort((a, b) => parseInt(a) - parseInt(b));

  // Filter grades
  const filteredGrades = grades.filter(grade => {
    const matchesSearch = grade.course.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesGrade = !gradeFilter || grade.grade === gradeFilter;
    const matchesYear = !yearFilter || grade.monthYear.endsWith(`/${yearFilter}`);
    const matchesMonth = !monthFilter || grade.monthYear.startsWith(`${monthFilter}/`);
    
    return matchesSearch && matchesGrade && matchesYear && matchesMonth;
  });

  // Sort grades
  const sortedGrades = [...filteredGrades].sort((a, b) => {
    switch (sortBy) {
      case 'date-asc':
        return compareDates(a.monthYear, b.monthYear);
      case 'date-desc':
        return compareDates(b.monthYear, a.monthYear);
      case 'grade-asc':
        return compareGrades(a.grade, b.grade);
      case 'grade-desc':
        return compareGrades(b.grade, a.grade);
      case 'course-asc':
        return a.course.localeCompare(b.course);
      case 'course-desc':
        return b.course.localeCompare(a.course);
      default:
        return 0;
    }
  });

  const filteredAndSortedGrades = sortedGrades;

  function compareDates(date1, date2) {
    const [month1, year1] = date1.split('/').map(Number);
    const [month2, year2] = date2.split('/').map(Number);
    if (year1 !== year2) return year1 - year2;
    return month1 - month2;
  }

  function compareGrades(grade1, grade2) {
    const gradeValues = {
      'A': 7, 'A-': 6,
      'B+': 5, 'B': 4, 'B-': 3,
      'C+': 2, 'C': 1, 'C-': 0,
      'D': -1, 'F': -2, 'INC': -3
    };
    return (gradeValues[grade1] || -4) - (gradeValues[grade2] || -4);
  }

  const handleClearFilters = () => {
    setSearchTerm('');
    setGradeFilter('');
    setYearFilter('');
    setMonthFilter('');
    setSortBy('date-desc');
  };

  return (
    <div className="grades-page">
      <div className="grades-header">
        <button onClick={onBack} className="back-button">
          ← Back to Dashboard
        </button>
        <h1>All Grades</h1>
        <p className="subtitle">Complete academic transcript with filtering and sorting</p>
      </div>

      <div className="filters-section">
        <div className="filters-grid">
          <div className="filter-group">
            <label>Search Course</label>
            <input
              type="text"
              placeholder="Search by course name..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="filter-group">
            <label>Grade</label>
            <select value={gradeFilter} onChange={(e) => setGradeFilter(e.target.value)}>
              <option value="">All Grades</option>
              {uniqueGrades.map(grade => (
                <option key={grade} value={grade}>{grade}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Year</label>
            <select value={yearFilter} onChange={(e) => setYearFilter(e.target.value)}>
              <option value="">All Years</option>
              {uniqueYears.map(year => (
                <option key={year} value={year}>20{year}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Month</label>
            <select value={monthFilter} onChange={(e) => setMonthFilter(e.target.value)}>
              <option value="">All Months</option>
              {uniqueMonths.map(month => (
                <option key={month} value={month}>
                  {new Date(2000, parseInt(month) - 1).toLocaleString('default', { month: 'long' })}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Sort By</label>
            <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
              <option value="date-desc">Date (Newest First)</option>
              <option value="date-asc">Date (Oldest First)</option>
              <option value="grade-desc">Grade (Highest First)</option>
              <option value="grade-asc">Grade (Lowest First)</option>
              <option value="course-asc">Course (A-Z)</option>
              <option value="course-desc">Course (Z-A)</option>
            </select>
          </div>
        </div>

        <button onClick={handleClearFilters} className="clear-filters-button">
          Clear Filters
        </button>
      </div>

      <div className="grades-table-container">
        {filteredAndSortedGrades.length > 0 ? (
          <table className="grades-table">
            <thead>
              <tr>
                <th>Course</th>
                <th>Grade</th>
                <th>Percentage</th>
                <th>Month / Year</th>
              </tr>
            </thead>
            <tbody>
              {filteredAndSortedGrades.map((grade, index) => (
                <tr key={index}>
                  <td data-label="Course">{grade.course}</td>
                  <td data-label="Grade">
                    <span className={`grade-badge grade-${grade.grade.replace('+', '\\+').replace('-', '-')}`}>
                      {grade.grade}
                    </span>
                  </td>
                  <td data-label="Percentage" className="percentage-cell">
                    {grade.percentage !== 'N/A' ? `${grade.percentage}%` : 'N/A'}
                  </td>
                  <td data-label="Month/Year">{grade.monthYear}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="no-grades">
            <p>No grades match your filters.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default GradesPage;
